import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const style = {
  table: {
    borderCollapse: 'collapse'
  },
  tableCell: {
    border: '1px solid gray',
    margin: 0,
    padding: '5px 10px',
    width: 'max-content',
    minWidth: '150px'
  },
  form: {
    container: {
      padding: '20px',
      border: '1px solid #F0F8FF',
      borderRadius: '15px',
      width: 'max-content',
      marginBottom: '40px'
    },
    inputs: {
      marginBottom: '5px'
    },
    submitBtn: {
      marginTop: '10px',
      padding: '10px 15px',
      border:'none',
      backgroundColor: 'lightseagreen',
      fontSize: '14px',
      borderRadius: '5px'
    }
  }
}

function PhoneBookForm(props) {

  const initContact = {
    id: null,
    userFirstName: "Coder",
    userLastName: "Byte",
    userPhone: "8885559999",
  };

  const [userState, setUserState] = useState(initContact);

  const handleUserChange = (e) => {
    setUserState({
      ...useState,
      [e.target.name]: e.targe.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if( !userState.userFirstname || !userState.userLastname || !userState.userPhone ){
      return;
    }
    props.addUser(userState);
    setUserState(initContact);
  };

  return (
    <form onSubmit={handleSubmit} style={style.form.container}>
      <label>First name:</label>
      <br />
      <input 
        style={style.form.inputs}
        className='userFirstname'
        name='userFirstname' 
        type='text'
        value={userState.userFirstName}
        onChange={handleUserChange}
      />
      <br/>
      <label>Last name:</label>
      <br />
      <input 
        style={style.form.inputs}
        className='userLastname'
        name='userLastname' 
        type='text' 
        value={userState.userLastName}
        onChange={handleUserChange}
      />
      <br />
      <label>Phone:</label>
      <br />
      <input
        style={style.form.inputs}
        className='userPhone' 
        name='userPhone' 
        type='text'
        value={userState.userPhone}
        onChange={handleUserChange}
      />
      <br/>
      <input 
        style={style.form.submitBtn} 
        className='submitButton'
        type='submit' 
        value='Add User' 
      />
    </form>
  )
}

function InformationTable(props) {
  if(props.users){
    const sortedContacts = props.users.sort((a,b) => a.userLastName.localeCompare(b.userLastName));
    if(sortedContacts){
      const display = sortedContacts.length > 0 ? (
        sortedContacts.map((user, index) => (
          <tr key={index}>
            <th style={style.tableCell}>{user.userFirstname}</th>
            <th style={style.tableCell}>{user.userLastname}</th>
            <th style={style.tableCell}>{user.userPhone}</th>
          </tr>
        ))
      ) : (
        <tr>
          <td colSpan={3}>&nbsp;</td>
        </tr>
      );
    }
  }

  return (
    <table style={style.table} className='informationTable'>
      <thead> 
        <tr>
          <th style={style.tableCell}>First name</th>
          <th style={style.tableCell}>Last name</th>
          <th style={style.tableCell}>Phone</th>
        </tr>
      </thead> 
    </table>
  );
}

function Application(props) {
  const usersObj = [];

  const[users, setUsers] = useState(usersObj);

  const addUser = (user) => {
    user.id = users.length + 1;
    setUsers([...users, user]);
  };

  return (
    <section>
      <PhoneBookForm />
      <InformationTable />
    </section>
  );
}

ReactDOM.render(
  <Application />,
  document.getElementById('root')
);
